/* -------------- Helpers -------------- */
function makeTable(headers, rows) {
  let html = '<table class="sql-output"><thead><tr>';
  headers.forEach(h => html += `<th>${h}</th>`);
  html += '</tr></thead><tbody>';
  rows.forEach(r => {
    html += '<tr>';
    r.forEach(c => html += `<td>${c}</td>`);
    html += '</tr>';
  });
  html += '</tbody></table>';
  return html;
}


